  render() {
    const { character, quote, image, characterDirection } =
      this.props.character;

    console.log(characterDirection);

    if (characterDirection === "Left") {
      return (
        <div className="character">
          <Name name={character} />
          <Image image={image} />
          <Quote quote={quote} />
          <Controls id={id} liked={liked} />
        </div>
      );
    }

    return (
      <div className="character">
        <Name name={character} />
        <Quote quote={quote} />
        <Image image={image} />
        <Controls id={id} liked={liked} />
      </div>
    );
  }
}

export default Character;
